"use strict";$(function(){var e=location.pathname,a=new RegExp("/","g"),c=e.match(a),s=c?c.length:0,t="";if(s>1)try{var i=e.indexOf("/");indexTwo=e.indexOf("/",i+1),t=e.substring(i,indexTwo)}catch(e){}else t=e;switch(t){case"/home":$(".headActive").eq(0).addClass("active");break;case"/ecology":$(".headActive").eq(1).addClass("active");break;case"/intellect":$(".headActive").eq(2).addClass("active");break;case"/college":$(".headActive").eq(3).addClass("active");break;case"/newslatest":$(".headActive").eq(4).addClass("active")}$(".lang").on("click",function(){$(this).addClass("active").siblings(".lang").removeClass("active")})});