"use strict";$(function(){var e=location.pathname,a=new RegExp("/","g"),s=e.match(a),c=s?s.length:0,i="";if(c>1){var t=e.indexOf("/");indexTwo=e.indexOf("/",t+1),i=e.substring(t,indexTwo)}else i=e;switch(i){case"/home":$(".headActive").eq(0).addClass("active");break;case"/ecology":$(".headActive").eq(2).addClass("active");break;case"/intellect":$(".headActive").eq(3).addClass("active");break;case"/college":$(".headActive").eq(4).addClass("active");break;case"/newslatest":$(".headActive").eq(5).addClass("active")}$(".lang").on("click",function(){$(this).addClass("active").siblings(".lang").removeClass("active")})});