"use strict";var id=$(".id").val();axios("/articleId?id="+id).then(function(t){var e=t.data.result.article;$(".title").text(e.title);var a=new Date(e.gmt_create),i=a.getFullYear(),l=a.getMonth()+1,r=a.getDate();l=l<10?"0"+l:l,r=r<10?"0"+r:r,$(".date").text(i+"-"+l+"-"+r),$(".txt").html(e.content)}),axios("/recommend").then(function(t){var e=t.data.result.items,a="";for(var i in e)a+='<li><a href="/newslatest/detail?id='+e[i].id+'" title="'+e[i].title+'">'+e[i].title+"</a></li>";$(".listData").append(a)});